import React, { useState } from "react";
import CoinsItem from "./CoinsItem";
import "./DisplayCoins.css";
import "./Overlay.css"

function DisplayCoins(props) {
  const [search, setSearch] = useState("");
  const [refresh, setRefresh] = useState("");

  function searchHandler(e) {
    setSearch(e.target.value);
  }

  function refreshHandler(e){
    e.preventDefault();
    
  }

  return (
    <div className="container">
      <div>
        <input
          onChange={searchHandler}
          className="search"
          type="text"
          placeholder="Search..."
        />
        <button onClick={refreshHandler} className="ref-btn">Refresh</button>
        <div className="heading">
          <p>#</p>
          <p className="coin-name">Coin</p>
          <p>Price</p>
          <p>24th</p>
          <p className="hide-mobile">Volume</p>
          <p className="hide-mobile">Mkt Cap</p>
        </div>
        {props.coins.filter((val) => {
            if(search === ''){
                return val;
            }else if(val.name.toLowerCase().includes(search.toLowerCase())){
                return val;
            }
        }).map((coins) => {
          return <CoinsItem coins={coins} key={coins.id} />;
        })}
      </div>
    </div>
  );
}

export default DisplayCoins;
