import React, { useState, useEffect } from "react";
import axios from "axios";
import DisplayCoins from "./DisplayCoins";
import "./Overlay.css"

function CoinsMarket() {
  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState('')

  const url =
    "https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=10&page=1&sparkline=false";

    function searchHandler(e){
      setSearch(e.target.value);
    }

  useEffect(() => {
    axios
      .get(url)
      .then((response) => {
        setCoins(response.data).filter((val) => {
          if(search == ''){
            return response.data;
          }else if(val.response.data.toLowerCase().includes(search.toLowerCase())){
            return val;
          }
        });
      })
      .catch((error) => {
      });
  }, []);

  return (
    <div>
      <input onChange={searchHandler} className="search" type="text" placeholder="Search..." />
      <button className="ref-btn">Refresh</button>
      <DisplayCoins coins={coins} />
    </div>
  );
}

export default CoinsMarket;
