import React from "react";
import CoinsItem from "./CoinsItem";
import './DisplayCoins.css'

function DisplayCoins(props){
    return (<div className="container">
        <div>
        <input onChange={searchHandler} className="search" type="text" placeholder="Search..." />
      <button className="ref-btn">Refresh</button>
            <div className="heading">
                <p>#</p>
                <p className="coin-name">Coin</p>
                <p>Price</p>
                <p>24th</p>
                <p className="hide-mobile">Volume</p>
                <p className="hide-mobile">Mkt Cap</p>
            </div>
            {props.coins.map(coins => {
                return (
                    <CoinsItem coins={coins} key={coins.id} />
                )
            })}
        </div>
    </div>)
}

export default DisplayCoins;
